// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.24;

import {LendingPoolFactory} from "./LendingPoolFactory.sol";

import {ERC20} from "solmate/src/tokens/ERC20.sol";
// import "solmate/src/tokens/ERC20.sol";
import {ERC4626} from "solmate/src/mixins/ERC4626.sol";
// import "solmate/src/mixins/ERC4626.sol";
import {Auth, Authority} from "solmate/src/auth/Auth.sol";

// import {PriceOracle} from "./interface/PriceOracle.sol";
// import {InterestRateModel} from "./interface/InterestRateModel.sol";
// import {FlashBorrower} from "./interface/FlashBorrower.sol";

import {SafeTransferLib} from "solmate/src/utils/SafeTransferLib.sol";
import {SafeCastLib} from "solmate/src/utils/SafeCastLib.sol";
import {FixedPointMathLib} from "solmate/src/utils/FixedPointMathLib.sol";

interface FlashBorrower {
    /// @dev Called when a flash loan is created.
    function execute(uint256 amount, bytes memory data) external;
}

/**
 * @title Interest Rate Module Interface
 * @author Compound
 */
interface InterestRateModel {
    /**
     * @notice Calculates the current borrow interest rate per block
     * @param cash The total amount of cash the market has
     * @param borrows The total amount of borrows the market has outstanding
     * @param reserves The total amount of reserves the market has
     * @return The borrow rate per block (as a percentage, and scaled by 1e18)
     */
    function getBorrowRate(
        uint256 cash,
        uint256 borrows,
        uint256 reserves
    ) external view returns (uint256);

    /**
     * @notice Calculates the current supply interest rate per block
     * @param cash The total amount of cash the market has
     * @param borrows The total amount of borrows the market has outstanding
     * @param reserves The total amount of reserves the market has
     * @param reserveFactorMantissa The current reserve factor the market has
     * @return The supply rate per block (as a percentage, and scaled by 1e18)
     */
    function getSupplyRate(
        uint256 cash,
        uint256 borrows,
        uint256 reserves,
        uint256 reserveFactorMantissa
    ) external view returns (uint256);
}

// TODO: Should not have to import ERC20 from here.
